import React, { useState } from "react";
import cream_rollLogo from '../assets/cream_rollLogo.jpg';
import diff_icecream from '../assets/diff_icecream.png';
import ingre_2_icecream from '../assets/ingre_2_icecream.jpg';


function Home() {
  const [index, setIndex] = useState(0);
  const picList = [cream_rollLogo, diff_icecream, ingre_2_icecream];

  const onClickNext = () => {
    if (index + 1 === picList.length) {
      setIndex(0);
    } else {
      setIndex(index + 1);
    }
  };

  const onClickPrevious = () => {
    if (index - 1 === -1) {
      setIndex(picList.length - 1);
    } else {
      setIndex(index - 1);
    }
  };

  return (
    <div>
      <div className="App">
      <header className="App-header">
        <h1> Scrumptious Ice Cream </h1>
        <h4 style={{ color: "rgb(7, 21, 43)", fontSize: "20px", textAlign: "center" }}>
          you choose the ingredient, we provide the recipes
        </h4>

        <br />

        <img src={picList[index]} style={{ "height": "400px", "width": "400px" }} alt='' /> <br />
        
        <div style={{ display: "flex", padding:"30px" }}>
          <button style={{ fontSize: "18px" }} onClick={onClickPrevious}>
            Previous
          </button>
          <button style={{ marginLeft: "5px", fontSize: "18px" }} onClick={onClickNext}>
            Next
          </button>
        </div>

        <p style={{ 
            fontSize: "24px", 
            textAlign: "center", 
            color: "black" 
              }}>

          For deliciously tantalising and healthy <br></br>
          home-made ice cream for every occassion <br></br>
          Pop over to the
          </p>
          <hrh></hrh>

        <a
          className="App-link"
          href="https://www.bbcgoodfood.com/recipes/collection/ice-cream-recipes"
          target="_blank"
          rel="noopener noreferrer"
        >
          recipes 
        </a><br></br>

      </header>
      </div>
    </div>
  );
}

export default Home;
