import React, { useState } from "react";
import cream_rollLog from '../assets/cream_rollLogo.jpg';
import diff_icecream from '../assets/diff_icecream.png';
import ingre_2_icecream from '../assets/ingre_2_icecream.jpg';
import icecream_sundae from '../assets/icecream_sundaejpg.jpg';

function Home() {
  const [selectedImg, setSelectedImg] = useState(cream_rollLogo);

  const handleImgClick = (img) => {
    setSelectedImg(img);
  };

  const picList = [cream_roll_log, diff_icecream, ingre_2_icecream, icecream_sundae];
  const [index, setIndex] = useState(0);

  const onClickNext = () => {
    setIndex((index + 1) % picList.length);
    setSelectedImg(picList[(index + 1) % picList.length]);
  };

  const onClickPrevious = () => {
    setIndex((index - 1 + picList.length) % picList.length);
    setSelectedImg(picList[(index - 1 + picList.length) % picList.length]);
  };

  return (
    <div>

<div className="App">
        <header className="App-header">
          <h1>Scrumptious Ice Cream</h1>
          <h4 style={{ color: "rgb(7, 21, 43)", fontSize: "20px", textAlign: "center" }}>
            you choose the ingredient, we provide the recipes
          </h4>
          <br/>

        <div>
          <img
            src={selectedImg}
            alt="Selected Images"
            style={{ height: "400px", width: "500px" }}
          />
        </div>

        <div>
          <img
            src={cream_rollLogo}
            alt="cream roll ice cream"
            style={{ height: "100px", width: "100px", margin: "5px" }}
            onClick={() => handleImgClick(cream_rollLogo)}
          />
          <img
            src={diff_icecream}
            alt="2 ingredient ice cream"
            style={{ height: "100px", width: "100px", margin: "5px" }}
            onClick={() => handleImgClick(diff_icecream)}
          />
          <img
            src={ingre_2_icecream}
            alt="roll ice cream"
            style={{ height: "100px", width: "100px", margin: "5px" }}
            onClick={() => handleImgClick(ingre_2_icecream)}
          />
          <img
            src={icecream_sundae}
            alt="icecream_sundae"
            style={{ height: "100px", width: "100px", margin: "5px" }}
            onClick={() => handleImgClick(icecream_sundae)}
          />
        </div>

     
          <div style={{ display: "flex", padding:"30px" }}>
            <button style={{ fontSize: "18px" }} onClick={onClickPrevious}>
              Previous
            </button>
            <button style={{ marginLeft: "5px", fontSize: "18px" }} onClick={onClickNext}>
              Next
            </button>
          </div>

          <p style={{ 
          fontSize: "24px", 
          textAlign: "center", 
          color: "black" 
            }}>

          Deliciously Tantalising Healthy <br></br>
          home-made ice cream for every occassion <br></br>
          Pop over to the
        </p>
        
        <a
          className="App-link"
          href="https://www.bbcgoodfood.com/recipes/collection/ice-cream-recipes"
          target="_blank"
          rel="noopener noreferrer"
        >
        recipes 
      </a><br></br>

      </header>
       </div>
   </div>
);
}


export default Home;
