import React, { useState } from "react";
import cream_rollLogo from '../assets/cream_rollLogo.jpg';
import diff_icecream from '../assets/diff_icecream.png';
import ingre_2_icecream from '../assets/ingre_2_icecream.jpg'

// import cream_rollLogo from '../images/cream_rollLogo.jpg';
// import diff_icecream from '../images/diff_icecream.png';
// import ingre_2_icecream from '../images/ingre_2_icecream.jpg';
import './App.css';

// C:\Users\sario\OneDrive\Code 1st Girls\ice-app\src\assets\cream_rollLogo.jpg

function Home() {
  const [index, setIndex] = useState(0);
  const picList = [cream_rollLogo, diff_icecream, ingre_2_icecream];

  const onClickNext = () => {
    if (index + 1 === picList.length) {
      setIndex(0);
    } else {
      setIndex(index + 1);
    }
  };

  const onClickPrevious = () => {
    if (index - 1 === -1) {
      setIndex(picList.length - 1);
    } else {
      setIndex(index - 1);
    }
  };

  return (
    <div>
      <div className="App">
      <header className="App-header">
        <h1>Welcome to Me Site</h1><br />
        <br />

        <img src={picList[index]} style={{ "maxHeight": "40%", "maxWidth": "40%" }} alt='' /> <br />
        <button style={{ "fontSize": "18px" }} onClick={onClickPrevious}> Previous </button>
        <button style={{ "marginLeft": "5px", "fontSize": "18px" }} onClick={onClickNext}> Next </button>

        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://www.bbcgoodfood.com/recipes/collection/ice-cream-recipes"
          target="_blank"
          rel="noopener noreferrer"
        >
          Pop over to the recipes
        </a>

      </header>
      </div>
    </div>
  );
}

export default Home;
